package com.example.lab21import android.content.Intentimport android.os.Bundleimport android.widget.ArrayAdapterimport android.widget.Toastimport androidx.appcompat.app.AppCompatActivityimport androidx.core.view.isEmptyimport kotlinx.android.synthetic.main.activity_main.*var dataArray = ArrayList<String>()class MainActivity : AppCompatActivity(){    override fun onCreate(savedInstanceState: Bundle?)    {        super.onCreate(savedInstanceState)        setContentView(R.layout.activity_main)        var adapter = ArrayAdapter(this,android.R.layout.simple_list_item_1,dataArray)        var idListViewClicked: Int = -1        listView.adapter = adapter        addButton.setOnClickListener()        {            val intent = Intent(this, LoginActivity2::class.java)            startActivity(intent)        }        listView.setOnItemClickListener{parent,view,position,id ->            idListViewClicked = position        }        delButton.setOnClickListener()        {            if (listView.isEmpty() || idListViewClicked == -1)            {                Toast.makeText(this, "Please, select row", Toast.LENGTH_SHORT).show()            }            else            {                dataArray.removeAt(idListViewClicked)                adapter.notifyDataSetChanged()            }        }    }    override fun onResume()    {        super.onResume()        val strEmail: String? = intent.getStringExtra("e")        val strPassword: String? = intent.getStringExtra("p")        if (strPassword != null && strEmail != null)        {            dataArray.add("$strEmail - $strPassword")        }    }}